    private void btnCalculatorActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculatorActionPerformed
        String amount = txtAmount.getText();
        String tax = txtTax.getText();
        float amount_float = Float.parseFloat(amount);
        float tax_float = Float.parseFloat(tax);
        float total;
        total = amount_float + tax_float*amount_float/100;
        total = Math.round(total);
        JOptionPane.showMessageDialog(this,"Chicago tax is " + tax +"%, total amount: " + total);
        
        
    }//GEN-LAST:event_btnCalculatorActionPerformed

