    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String usuario = jTextField1.getText();
        String paswd =jPasswordField1.getText();
        
        if(usuario.isEmpty() || paswd.isEmpty()){
            JOptionPane.showMessageDialog(null,"Some field is empty");
                       
        }else{
            if(usuario.equals("usuario")&& paswd.equals("1234")){
                JOptionPane.showMessageDialog(null,"Hello");
                SPInventory SP = new SPInventory();
                SP.setVisible(true);
                this.dispose();
                
                
            }else{
                JOptionPane.showConfirmDialog(null,"wrong username or password");
            }
        }
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

