    private void BtnCalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnCalculateActionPerformed
        double priceTotalTaxes;
        double objectTax;
        double taxValueInNewYork;
        double sale;
       
        sale = Double.parseDouble(txtSale.getText());
        taxValueInNewYork = Double.parseDouble(txtTaxes.getText());
        objectTax = sale * (taxValueInNewYork/100);
        priceTotalTaxes= sale + objectTax;
        
        DecimalFormat format = new DecimalFormat("#.00");
        JOptionPane.showMessageDialog(null, "The entire amount to pay in New York State including the 4% tax is "
                + format.format(priceTotalTaxes) + " dollars");
    }//GEN-LAST:event_BtnCalculateActionPerformed

