    private void jTextFieldNumbersOrderActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jTextFieldNumbersOrderActionPerformed
      input=new Scanner(System.in);
        //System.out.print("Enter number are you goin to order: ");
        int cant;
        cant=input.nextInt();
        data=new int[cant];
        for(int f=0;f<data.length;f++) {
            //System.out.print("Insert the numbers: ");
            data[f]=input.nextInt();
        }	
    }//GEN-LAST:event_jTextFieldNumbersOrderActionPerformed

