    private void btnAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddActionPerformed
    int id;
    String name;
    String hobby;
    String cellphoneNumber;
    String sex;
    String group;
    float salary;
    String birthDate;
    //LocalDate birthDate;
    String comments;
    
    
    id=Integer.valueOf(txtId.getText());
    name=txtName.getText();
    hobby=lstHobby.getToolTipText();
    cellphoneNumber =txtCellPhone.getText();
    sex=rbtnHombre.getText();
    sex=rbtnMujer.getText();
    group=cmbGroup.getToolTipText();
    salary=Float.valueOf(txtSalary.getText());
    comments=txtaComments.getText();
    birthDate=txtBirthDate.getDateFormatString();
    
    Contact contact =new Contact(id, name, hobby, cellphoneNumber, sex, group, salary,/* birthDate, */birthDate,comments);
            
    }//GEN-LAST:event_btnAddActionPerformed

