    private void btnRegisterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegisterActionPerformed
    String lastName;
    LastName = txtLastName.getText();
    int option;
    option = JOptionPane.showConfirmDialog(this, "Are you sure you want to register");
    if(option ==0){
    JOptionPane.showMessageDialog(this,"Registrering" + lastName);
    }else if(option == JOptionPane.NO_OPTION){
        JOptionPane.showMessageDialog(THIS, lastName + "was not registered");
    }
    }//GEN-LAST:event_btnRegisterActionPerformed

    private void btnFindActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnFindActionPerformed
        String cedula;
        cedula = JOptionPane.showInputDialog("Enter your Cedula");
        txtCedula.setText("Espejo");
    }//GEN-LAST:event_btnFindActionPerformed

    public String readInputs(){
    cedula = txtCedula.getText();
    lastName = txtLastName.getText();
    
    String data;
    data = cedula + "\n" + LastName + "\n";
    return data;
    }
