    private void btnCalculateActionPerformed(java.awt.event.ActionEvent evt) {                                             
        Double capital, reason, time , interest, amount;
        
        capital=Double.parseDouble(txtCapital.getText());
        time=Double.parseDouble(txtTime.getText());
        reason = Double.parseDouble(txtInterestRate.getText());
        
        amount = Math.pow(1+reason,time)*capital;
        interest= amount-capital;
        
        txtInterest.setText(Double.toString(interest));
        txtAmount.setText(Double.toString(amount)); 
    }                                            

    private void btnCancelActionPerformed(java.awt.event.ActionEvent evt) {                                          
        txtCapital.setText("");
        txtTime.setText("");
        txtInterestRate.setText("");
        txtAmount.setText("");
        txtInterest.setText("");
        FrmSavingAccount open = new FrmSavingAccount ();
        open.setVisible(true);
        this.setVisible (false);
    }                                         

