    public void enableButton() {
        if (!txtCapital.getText().isEmpty()) {
            btnCalculate.setEnabled(true);
            btnClean.setEnabled(true);

        } else {
            btnCalculate.setEnabled(false);
            btnClean.setEnabled(false);
        }

    }

    public void initButtons() {
        btnCalculate.setEnabled(false);
        btnClean.setEnabled(false);
        txtAmount.setEditable(false);
        txtInterest.setEditable(false);
    }

    public void rangeInterestRate() {
        SpinnerNumberModel range = new SpinnerNumberModel();
        range.setMaximum(12);
        range.setMinimum(0);
        spiTime.setModel(range);
    }
    private void btnCalculateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalculateActionPerformed
        Double capital, time, interest, amount;
        int reason = sliderInterest.getValue();

        capital = Double.parseDouble(txtCapital.getText());
        time = Double.parseDouble(spiTime.getValue().toString());

        amount = Math.pow(1 + reason, time) * capital;
        interest = amount - capital;

        txtInterest.setText(Double.toString(Math.round(interest)));
        txtAmount.setText(Double.toString(Math.round(amount)));

        lblInfCapital.setText("");
    }//GEN-LAST:event_btnCalculateActionPerformed

    private void btnCleanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCleanActionPerformed
        txtCapital.setText("");
        spiTime.setValue(0);
        sliderInterest.setValue(0);
        txtAmount.setText("");
        txtInterest.setText("");
        lblInfCapital.setText("");
    }//GEN-LAST:event_btnCleanActionPerformed

    private void btnReturnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnReturnActionPerformed
        FrmSavingAccount open = new FrmSavingAccount();
        open.setVisible(true);
        this.setVisible(false);
    }//GEN-LAST:event_btnReturnActionPerformed

    private void sliderInterestStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_sliderInterestStateChanged
        int value = sliderInterest.getValue();
    }//GEN-LAST:event_sliderInterestStateChanged

    private void txtCapitalKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtCapitalKeyReleased
        enableButton();
        lblInfCapital.setText("Use the point for tenths");
    }//GEN-LAST:event_txtCapitalKeyReleased

